<?phprequire_once(STYLESHEETPATH . '/includes/wordpress-tweaks.php');// Load custom visual composer templatesapex_loadVCTemplates();$designWidth = 1600;$apexAdjustStylesheet = 'understrap-theme';add_action( 'wp_enqueue_scripts', 'rk_enqueue_styles');function rk_enqueue_styles() {    wp_enqueue_style( 'understrap-theme', get_stylesheet_directory_uri() . '/css/child-theme.css?' . filemtime(get_stylesheet_directory() . '/css/child-theme.css'));    wp_enqueue_style( 'google_font_source_sans_pro', 'https://fonts.googleapis.com/css?family=Source+Sans+Pro');    wp_enqueue_script( 'masonry', get_stylesheet_directory_uri() . '/node_modules/masonry-layout/dist/masonry.pkgd.js');    wp_enqueue_script( 'waypoint', get_stylesheet_directory_uri() . '/node_modules/waypoints/lib/noframework.waypoints.min.js');    wp_enqueue_script('understap-theme-js', get_stylesheet_directory_uri() . '/js/theme.js?' . filemtime(get_stylesheet_directory() . '/js/theme.js'), array('jquery'));}function understrap_remove_scripts() {    wp_dequeue_style( 'understrap-styles' );    wp_deregister_style( 'understrap-styles' );    wp_dequeue_script( 'understrap-scripts' );    wp_deregister_script( 'understrap-scripts' );    // Removes the parent themes stylesheet and scripts from inc/enqueue.php}add_action( 'wp_enqueue_scripts', 'understrap_remove_scripts', 20 );add_filter( 'vc_load_default_templates', 'rk_vc_load_default_templates' ); // Hook infunction rk_vc_load_default_template( $data ) {    return array();}add_action('init', 'rk_register_menus');function rk_register_menus() {    register_nav_menus(        Array(            'footer-menu' => __('Footer Menu'),        )    );}function rk_get_custom_field($id, $fieldIn) {    $field = 'wpcf-';    $field .= $fieldIn;    $details = get_post_meta($id);    return $details[$field];}function rk_get_projects($sort) {    // get projects    $args = array(        'post_type' => 'project',        'post_status' => 'publish',        'orderby' => 'post_date',        'order' => $sort    );    $result = array();    $result = new WP_Query($args);    return $result;}function projects_array() {    $projects = rk_get_projects('ASC');    $arr = array();    while ($projects->have_posts()) {        $projects->the_post();        $arr[] = $projects->post->ID;    }    return $arr;}function displayPrevButton($postIDIn) {    $projects = projects_array();    $key = (array_search($postIDIn,$projects) -1);    $html = '';    if($key >= 0) {        // get new project details        $project = get_post($projects[$key]);        $html = '<a href="' . get_page_link($project->ID) . '" class="btn btn-primary prev"><span class="fa fa-angle-left"></span>Previous Project</a>';    }    return $html;}function displayNextButton($postIDIn) {    $projects = projects_array();    $key = (array_search($postIDIn,$projects) +1);    $html = '';    if($key < count($projects)) {        // get new project details        $project = get_post($projects[$key]);        $html = '<a href="' . get_page_link($project->ID) . '" class="btn btn-primary next">Next Project<span class="fa fa-angle-right"></span></a>';    }    return $html;}function rk_get_brand_logos() {    $args = array(        'post_type' => 'brand',        'post_status' => 'publish',        'orderby' => '',        'order' => 'ASC',        'posts_per_page' => -1    );    $result = array();    $result = new WP_Query($args);    return $result;}